jQuery(document).ready(function($){var i=$(".cd-timeline-block");i.each(function(){$(this).offset().top>$(window).scrollTop()+.75*$(window).height()&&$(this).find(".cd-timeline-img, .cd-timeline-content").addClass("is-hidden")}),$(window).on("scroll",function(){i.each(function(){$(this).offset().top<=$(window).scrollTop()+.75*$(window).height()&&$(this).find(".cd-timeline-img").hasClass("is-hidden")&&$(this).find(".cd-timeline-img, .cd-timeline-content").removeClass("is-hidden").addClass("bounce-in")})})});